import React from 'react'

const Colors=(props)=> {
    const [penColor,penColorChange]=useState(COLORS.RED)

    
    return (
        <div className="colors-container" >
            <div onClick={()=>props.penColorChange(props.COLORS.RED)} className={(props.penColor===props.COLORS.RED)?"color-selected":"color"} id="color-red" />
            <div onClick={()=>props.penColorChange(props.COLORS.YELLOW)} className={(props.penColor===props.COLORS.YELLOW)?"color-selected":"color"} id="color-yellow" />
            <div onClick={()=>props.penColorChange(props.COLORS.BLUE)} className={(props.penColor===props.COLORS.BLUE)?"color-selected":"color"} id="color-blue" />
            <div onClick={()=>props.penColorChange(props.COLORS.GREEN)} className={(props.penColor===props.COLORS.GREEN)?"color-selected":"color"} id="color-green" />
            <div onClick={()=>props.penColorChange(props.COLORS.ORANGE)} className={(props.penColor===props.COLORS.ORANGE)?"color-selected":"color"} id="color-orange" />
        </div>
    )
}

export default Colors
