import React,{useState} from 'react'

const COLORS ={
    RED:1,
    YELLOW:2,
    BLUE:3,
    GREEN:4,
    ORANGE:5
}

const Colors=()=> {
    const [penColor,penColorChange]=useState(COLORS.RED)

    const colorChanger =(val)=>{
        penColorChange(val)
        
    }

    return (
        <div className="colors-container" >
            <div onClick={()=>penColorChange(COLORS.RED)} className={(penColor===COLORS.RED)?"color-selected":"color"} id="color-red" />
            <div onClick={()=>penColorChange(COLORS.YELLOW)} className={(penColor===COLORS.YELLOW)?"color-selected":"color"} id="color-yellow" />
            <div onClick={()=>penColorChange(COLORS.BLUE)} className={(penColor===COLORS.BLUE)?"color-selected":"color"} id="color-blue" />
            <div onClick={()=>penColorChange(COLORS.GREEN)} className={(penColor===COLORS.GREEN)?"color-selected":"color"} id="color-green" />
            <div onClick={()=>penColorChange(COLORS.ORANGE)} className={(penColor===COLORS.ORANGE)?"color-selected":"color"} id="color-orange" />
        </div>
    )
}

export default Colors
